import React, { Component } from 'react';
import defaultImg from '../images/Logomini.svg';
import { Link } from 'react-router-dom';
import _ from 'lodash';
import Modal from 'react-modal';
import Zoom from 'react-reveal/Zoom';
import Banner from './Banner';
import { MdExpandMore } from 'react-icons/md';


export class UserBar extends Component {
    constructor(){
        super()
        this.state = {
            user: {
                email: '',
                password: '',
                nickname: ''
            }
        }
    }
    onSubmit = (e) => {
        e.preventDefault();
        const { user } = this.state;

    }
    render() {
        const { modal } = this.state;
        const { store } = this.props;
        const me = store.getCurrentUser();
        const profileImg = _.get(me, 'avatar');
        return (
            <>
                <div className="user-bar">
                    { !me ? <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter" className="login-btn">Sign In</button> : null}
                    <div className="name-user">{_.get(me, 'name')}</div>
                    <img src={profileImg ? profileImg : defaultImg} alt="user-img" className="img-user"></img>
                </div>

                    <div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                    <div class="modal-dialog modal-dialog-centered" role="document">
                        <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <div className="user-form">
                                 <form onSubmit= {this.onSubmit}>
                                    <ul className="form-container">
                                        <li>
                                            <h3>Sign In / Register Your Account</h3>
                                        </li>
                                        <li>
                                            <label htmlFor="email">
                                                Email
                                            </label>
                                            <input onChange={ this.onTxtfieldChange } type="email" name="email" id="email" ></input>
                                        </li>
                                        <li>
                                            <label htmlFor="password">Password</label>
                                            <input onChange={ this.onTxtfieldChange } type="password" id="password" name="password" ></input>
                                        </li>
                                        <li>
                                            <button type="submit" className="login-btn">Signin</button>
                                        </li>
                                        <li>
                                            <button  className="login-btn" >Create new account?</button>
                                        </li>
                                    </ul>
                                    </form>
                                
                                 </div>
                            </div>
                        {/* <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Save changes</button>
                        </div> */}
                        </div>
                    </div>
                    </div>
                
            </>
        )
    }
}

export default UserBar
