import React, { Component } from 'react'
import { Avatar, IconButton, InputBase } from "@material-ui/core";
import PropTypes from 'prop-types';


export class SidebarUsers extends Component {
    static propTypes = {
        name: PropTypes.string.isRequired,
        lastMessage: PropTypes.string,
        active:PropTypes.bool,
        onClick: PropTypes.func
    }
    static defaultProps = {
        lastMessage:"",
        active: false,
        onClick: () => {}
    }
    render(){
        const { name, lastMessage, active, onClick } = this.props
        return (
            <div className="sidebarChat" onClick={ ()=>{ onClick } }>
                <Avatar>
                    {name[0].toUpperCase()}
                </Avatar>
                
                <div className="sidebarChat_info">
                    <h4 className="name">{name}</h4>
                    {lastMessage ? <p className="last-message">{lastMessage}</p> : "none.."}
                </div>
            </div>
        )
    }
   
}
