import Sidebar from './Sidebar';
import Chat from './Chat';
import io from 'socket.io-client';
import { USER_CONNECTED, LOGOUT } from '../Events';
import { LoginChat } from './LoginChat';

const socketUrl = "http://192.168.1.3:5000"


export default class App3 extends Component {
   constructor(props) {
       super(props);
       this.state = {
        socket: null,
        user: null
       };
   }
   componentWillMount() {
    this.initSocket()
}
   
   initSocket = () => {
       const socket = io(socketUrl)
       socket.on('connect', ()=>{
           //
       })
       this.setState({socket})
   }
        

   setUser =( user ) => {
       const { socket } = this.state
       socket.emit(USER_CONNECTED);
       this.setState({user})
   }
   
   logout = () => {
       const { socket } =this.state
       socket.emit(LOGOUT)
       this.setState({user:null})
   }
    render() {

      
     
        return (
            <div className="app3">
            {/* Sidebar */}
            <Sidebar/>
            {/* Chat Component */}
            <Chat />
            {/* Login chat */}
            <LoginChat socket={socket} setUser={this.setUser} />
        </div>
        )
    }
}
