import React, { Component } from 'react'
import defaultImg from '../images/Logomini.svg';
import { Link } from 'react-router-dom';
import { OrderedMap } from 'immutable';


export default class App3 extends Component {
    constructor(props){
        super(props);
        this.state = {
            height: window.innerHeight,
        }

    }
    _onResi = () => {
        this.setState({height: window.innerHeight});
    }
    addTestMessages = () => {
        const {store} = this.props

      
        for(let i = 0; i < 20; i++){
            let isMe = false;
            if(i % 2 === 0) {
                isMe = true;
            }
            const newMsg = {
                _id: `${i}`,
                author: `Author: ${i}`,
                body: `The body of message ${i}`,
                avatar: defaultImg,
                me: isMe,
                timing: '0:00'
            }
            store.addMessage(i, newMsg);

        }

        for (let c = 0; c<10; c++){
            const newChannel ={
                _id: `${c}`,
                title: `Channel title ${c}`,
                lastMessage: `Hey there is.. ${c}`,
                members: new OrderedMap({
                    '1': true,
                    '2': true,
                }),
                messages: new OrderedMap({
                    '5': true,
                    '6': true,
                    '7': true
                }),
            }
            store.addChannel(c, newChannel);
        }

    }
    componentDidMount(){
        window.addEventListener('resize', this._onResi);
        this.addTestMessages();
    }
    componentWillUnmount(){
        window.removeEventListener('resize', this._onResi);
    }
    render() {
        const { height } = this.state;
        const { store } = this.props;
        const activeChannel = store.getActiveChannel();
        const messages = store.getMessagesFromChannel(activeChannel);
        const chanels = store.getChannels();
        const style = {
            height: height,
        }
        return (
            <div style={style} className="app3">
                <div className="chat-header">
                    <div className="header-left">
                        <div className="action">
                            <Link className="btn-primary">New Message</Link>
                        </div>
                        </div>
                        <div className="header-mid">
                            <h3>Title</h3>
                        </div>
                        <div className="header-right">
                            <div className="user-bar">
                                <div className="name-user">nDs</div>
                                <img src={defaultImg} alt="user-img" className="img-user"></img>
                            </div>
                        </div>
                </div>
                <div className="chat-main">
                    <div className="sidebar-left">
                        <div className="chanels">
                            {chanels.map((chanel, index) =>{
                                return (
                                    <div onClick= {(index) => {
                                        store.setActiveChannel(chanel._id);
                                    }} key={chanel._id} className="chanel">
                                        <div className="user-img">
                                            <img src={defaultImg} alt="user-img" />
                                        </div>
                                        <div className="chanel-info">
                                            <h4>{chanel.title}</h4>
                                            <p>{chanel.lastMessage}</p>
                                        </div>
                                    </div>
                                )
                            })}
                            
                        </div>
                    </div>
                    <div className="chat-content">
                        <div className="messages">
                            { 
                                messages.map((mess, index) => { 
                                    return (
                                        <div key={index} className={`${mess.me ? 'message-me' : 'message'}`}>
                                        <img src={mess.avatar} alt="user-img" className="img-user"></img>
                                        <div className="message-body">
                                            <div className="username">{`${mess.me ? 'Me' : mess.author}`} say:</div>
                                            <p className="message-text">
                                                {mess.body}
                                                <p className="timing">{mess.timing}</p>
                                            </p>
                                        </div>
                                        </div>
                                    );
                                })
                            }
                           
                            {/*  <div className="message-me">
                                 <img src={defaultImg} alt="user-img" className="img-user"></img>
                                 <div className="message-body">
                                     <div className="username">Me say:</div>
                                     <p className="message-text">
                                         Hello you come app chat!
                                         <p className="timing">0:00</p>
                                     </p>
                                   
                                 </div>
                             </div> */}
                        </div>
                        <div className="message-input">
                            <form 
                                onSubmit={ this.handleSubmit }
                                className="message-form">

                                <input 
                                    id = "message"
                                    ref = {"messageinput"}
                                    type = "text"
                                    className = "form-control"
                                    // value = { message }
                                    autoComplete = {'off'}
                                    placeholder = "Type something interesting"
                                    // onKeyUp = { e => { e.keyCode !== 13 && this.sendTyping() } }
                                    // onChange = {
                                    // 	({target})=>{
                                    // 		this.setState({message:target.value})
                                    // 	}
                                    // }
                                    />
                                <button
                                    // disabled = { message.length < 1 }
                                    type = "submit"
                                    className = "send"

                                > Send </button>
                            </form>
			            </div>
                    </div>
                   
                    <div className="sidebar-right">
                        <div className="title-right">Members</div>
                        <div className="chanels">
                            <div className="chanel">
                                <div className="user-img">
                                    <img src={defaultImg} alt="user-img" />
                                </div>
                                <div className="chanel-info">
                                    <h4>nDs</h4>
                                    <p>Hello here..</p>
                                </div>
                            </div>
                        </div>
                        <div className="chanels">
                            <div className="chanel">
                                <div className="user-img">
                                    <img src={defaultImg} alt="user-img" />
                                </div>
                                <div className="chanel-info">
                                    <h4>nDs</h4>
                                    <p>Hello here..</p>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>

            </div>
        )
    }
}
