import React, {useEffect} from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components';
// import Hero from './Hero';
import Banner from './Banner';
// import Main1 from './Main1';
import axios from 'axios'
import Content1 from './Content1';
import Search1 from './Search1';



const App1 = (props) => {
  const api_call = async e => {
    e.prevenDefault();
    const API_KEY = "cc8f77093d29a14ebde27ae45bf28497";
    const url = `https://api.openweathermap.org/data/2.5/weather?q=Liverpool&appid=${API_KEY}`;
    const req = axios.get(url) ;
    const res = await req ;

  }
  useEffect(() => {
    api_call()
   
  }, [])
    return (
        <Wrapper>
        <div className='container'>
        <Banner title="Welcome to Weather App" subtitle="Where you pass the mind?">
        <Content1>
        <Search1 api_call={api_call}/>
        </Content1>
        </Banner>
        </div>
      </Wrapper>
    );
  };
  const Wrapper = styled.section`
    min-height: 100vh;
    display: grid;
    background: var(--clr-grey-5);
    place-items: center;
    .container {
      width: 90vw;
      max-width: 600px;
      text-align: center;
    }
  `;
export default App1
