import React, {useState} from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components';
// import Hero from './Hero';
import Banner from './Banner';
// import Main1 from './Main1';
import axios from 'axios'
import Content1 from './Content1';
import Detail1 from './Detail1';
import Search1 from './Search1';
import Context1 from '../Context1';
import StyledHero from './StyleHero';




const App1 = () => {
  const [weathers, setWeather] = useState();
  const [coording, setCoord] = useState();
  const [wind, setWind] = useState(); 
  let [img, setImg] = useState("");
    const api_call = async e => {
    e.preventDefault();
    const location = e.target.elements.location.value;
    const API_KEY = "cc8f77093d29a14ebde27ae45bf28497";
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${API_KEY}`;
    const req = axios.get(url) ;
    const res = await req ;
    // setWeather(res.data.main);
    setCoord(res.data.coord);
    setWind(res.data.wind);
    setWeather(res.data.main);
    // 
    if( ((res.data.main.temp).toFixed(2) > 15.00) )
    {
      setImg('../images/defaultBcg.jpg');
    }
    else {
      setImg('../images/defaultBcg.jpg');
    }
  }
 
  // useEffect(() => {
  //   api_call()
   
  // }, [])
    return (
      <StyledHero img = {img}>
         <Wrapper>
          <div className='container'>
            <Banner title="Welcome to Weather App" subtitle="Where you pass the mind?">
              <Content1>
                <Context1.Provider value={{ api_call, weathers, coording, wind }}>
                  <Search1 api_call={api_call}/>
                  {weathers && <Detail1 /> }
                  </Context1.Provider>
              </Content1>
            </Banner>
          </div>
      </Wrapper>
      </StyledHero>
       
    );
  };
  const Wrapper = styled.section`
    min-height: 100vh;
    display: grid;
    /* background: {img}; */
    place-items: center;
    .container {
      width: 90vw;
      max-width: 600px;
      text-align: center;
    }
  `;
export default App1
