import React, { Component } from 'react';
import defaultImg from '../images/Logomini.svg';

export class UserBar extends Component {
    constructor(){
        super()
        this.state = { time: new Date() }
    }
    loopTime()
    {
        this.setState({
            time: new Date()
        })
    }
    componentDidMount()
    {
        setInterval(() =>
            this.loopTime(), 1000
        )
    }
    render() {
        return (
            <>
                <div className="user-bar">
                    <div className="name-user">nDs</div>
                    <img src={defaultImg} alt="user-img" className="img-user"></img>
                </div>
            </>
        )
    }
}

export default UserBar
