import React, { Component } from 'react'
import Sidebar from './Sidebar';
import { MESSAGE_SENT, TYPING, COMMUNITY_CHAT, MESSAGE_RECIEVED, PRIVATE_MESSAGE,
        USER_CONNECTED, USER_DISCONNECTED, NEW_CHAT_USER } from '../Events';
import ChatHeading from "../messages/ChatHeading";
import Messages from "../messages/Messages";
import MessageInput from "../messages/MessageInput";
import { values, difference, differenceBy } from "lodash"

export default class ContentChat extends Component {
    constructor(props) {
        super(props);	
      
        this.state = {
            chats:[],
            activeChat:null
        };
      }
  
      componentDidMount() {
          const { socket } = this.props
          socket.emit(COMMUNITY_CHAT, this.resetChat)
      }
  

      resetChat = (chat)=>{
          return this.addChat(chat, true)
      }
  

      addChat = (chat, reset)=>{
          const { socket } = this.props
          const { chats } = this.state
  
          const newChats = reset ? [chat] : [...chats, chat]
          this.setState({chats:newChats, activeChat:reset ? chat : this.state.activeChat})
  
          const messageEvent = `${MESSAGE_RECIEVED}-${chat.id}`
          const typingEvent = `${TYPING}-${chat.id}`
  
          socket.on(typingEvent, this.updateTypingInChat(chat.id))
          socket.on(messageEvent, this.addMessageToChat(chat.id))
      }
  
      addMessageToChat = (chatId)=>{
          return message => {
              const { chats } = this.state
              let newChats = chats.map((chat)=>{
                  if(chat.id === chatId)
                      chat.messages.push(message)
                  return chat
              })
  
              this.setState({chats:newChats})
          }
      }
  

      updateTypingInChat = (chatId) =>{
          return ({isTyping, user})=>{
              if(user !== this.props.user.name){
  
                  const { chats } = this.state
  
                  let newChats = chats.map((chat)=>{
                      if(chat.id === chatId){
                          if(isTyping && !chat.typingUsers.includes(user)){
                              chat.typingUsers.push(user)
                          }else if(!isTyping && chat.typingUsers.includes(user)){
                              chat.typingUsers = chat.typingUsers.filter(u => u !== user)
                          }
                      }
                      return chat
                  })
                  this.setState({chats:newChats})
              }
          }
      }
  
      sendMessage = (chatId, message)=>{
          const { socket } = this.props
          socket.emit(MESSAGE_SENT, {chatId, message} )
      }
  
   
      sendTyping = (chatId, isTyping)=>{
          const { socket } = this.props
          socket.emit(TYPING, {chatId, isTyping})
      }
  
      setActiveChat = (activeChat)=>{
          this.setState({activeChat})
      }
    render() {
        const { user, logout } = this.props
        const { chats, activeChat, users } = this.state
        return (
            <div className="app3">
                <Sidebar
                logout= { logout }
                chats= { chats }
                user= { user }
                users = { users }
                addUserToChat = { this.addUserToChat }
                removeUsersFromChat = { this.removeUsersFromChat }
                activeChat= { activeChat }
                setActiveChat= { this.setActiveChat }
                onSendPrivateMessage={ this.sendOpenPrivateMessage}
                />
                <div className="chat">
                    {
                        activeChat !== null ? (
                            <div className="chat_room">
                                <ChatHeading name={activeChat.name} />
                                <Messages
                                    messages= {activeChat.messages}
                                    user= {user}
                                    typingUsers= {activeChat.typingUsers}
                                />
                                <MessageInput
                                    sendMessage= {
                                        (message) => {
                                            this.sendMessage(activeChat.id, message)
                                        }
                                    }
                                    sendTyping= {
                                        (isTyping) => {
                                            this.sendTyping(activeChat.id, isTyping)
                                        }
                                    }
                                    />

                            </div>
                        ) : <div className="chat-room-choose">
                                <h3>Choose a chat to start chat</h3>
                            </div>
                    }
                </div>
            </div>
        );
    }
}
