import React from "react";
import {
  // FormControl,
  TextField,
  Button,
  Grid,
  // CircularProgress,
  LinearProgress,
  Typography,
  Divider
} from "@material-ui/core";
import { loadReCaptcha, ReCaptcha } from "react-recaptcha-v3";
import axios from "../../apis/axios";
// import { GlobalContext } from "../GlobalState";

let captchaToken;
const FeedbackApp = () => {
  // const [{}, dispatch] = useContext(GlobalContext);

  // const setSnackbarMsg = React.useCallback(
  //   data => {
  //     dispatch({ type: "setSnackbarMsg", snippet: data });
  //   },
  //   [dispatch]
  // );
  const [isSending, setIsSending] = React.useState(false);
  const formEl = React.useRef(null);
  const formElEmail = React.useRef(null);
  const submitFormEmail = async (e) => {
    setIsSending(true);
    const formData = new FormData(formElEmail.current);
    const email = formData.get("email");
    const message = formData.get("message");
    const token = captchaToken;
    
    e.preventDefault();

    const response = await fetch("http://localhost:9150/ndsapi/mail", { 
      method: 'POST', 
      headers: { 
          'Content-type': 'application/json'
      }, 
      body: JSON.stringify({email, message, token}) 
    }); 
      const resData = await response.json(); 
      if (resData.status === 'success'){
        setIsSending(false);
        formElEmail.current.reset();
        alert("Your Message Sent"); 
        this.resetForm()
    }else if(resData.status === 'fail'){
        setIsSending(false);
        formElEmail.current.reset();
        alert("Message failed to send please try again")
    }

  }

  const submitForm = async (e) => {
    // set state to sending
    setIsSending(true);
    const formData = new FormData(formEl.current);
    const name = formData.get("name");
    const email = formData.get("email");
    const message = formData.get("message");

    e.preventDefault();

    const post = {
      name: name,
      email: email,
      message: message,
      token: captchaToken,
      timestamp: new Date(),
      }

    await axios
          .post('/ndsapi/feedback', post)
          .then(function(response) {
            console.log(response.data.status);
            console.log(response.data.message);
            // also clear the form
            formEl.current.reset();
            // setSnackbarMsg(response.data.status);
            setIsSending(false);
          })
          .catch(function(error) {
            // console.log(error.response.status);
            if (error.response) {
              if (error.response.status === 429) {
                formEl.current.reset();

                // setSnackbarMsg("We accept limited feedback!");
              }
            }
            setIsSending(false);
          });
      };

  React.useEffect(() => {
    loadReCaptcha("6Le1toEUAAAAAITyNwqEMaz3hFAYzciSJDMomrgN");
  }, []);

  const verifyCallback = token => {
    console.log(token);
    captchaToken = token;
    const captchaBox = document.querySelector(".grecaptcha-badge");
    captchaBox.remove();
    // remove captcha badge
  };

  return (
    <div className="privacy-app">
      <header>
        <Typography variant="h4" gutterBottom={true}>
            Feedbacks{" "}
        </Typography>
      </header>
      <Divider />
      <br />
      <div className="header-feedback">
        <p>Chúng tôi luôn sẵn sàng nhận ý kiến đóng góp từ người dùng về những vấn đề cần hoàn thiện hoặc các lỗi xảy ra khi App vận hành mà nhóm phát triển chưa sửa được. 
        <br/>(We are always ready to receive feedback from users about issues that need to be completed or errors occurring when the App is running but the development team has not fixed it.)
        Báo cáo về các vấn đề vi phạm sử dụng mà người dùng phản hồi. Hiện có 2 phương thức để người dùng trao đổi với nhóm phát triển.</p>
        (Report of usage violations that users respond to. There are 2 methods for users to talk to the development team)
        <br /><br />
        <h4>Through the nDsApp Server(Thông qua Server nDsApp)</h4>
        <br />
        <i>Sử dụng mail đã xác minh để chúng biết bạn là ai và liên lạc lại bạn nếu cần (Khuyến Nghị Dùng)</i> 
        (Use verified mail to let them know who you are and get back to you if needed (Recommended))      
      </div>
      <div className="form-feedback">
        <Grid
          component="form"
          ref={formEl}
          onSubmit={submitForm}
          container
          required
          justify="center"
          style={{ width: "100%", margin: "0 auto", padding: '1rem' }}
        >
          <ReCaptcha
            sitekey="6Le1toEUAAAAAITyNwqEMaz3hFAYzciSJDMomrgN"
            action="action_name"
            verifyCallback={verifyCallback}
          />
          <TextField
            id="outlined-email-input"
            label="Name"
            type="text"
            name="name"
            autoComplete="name"
            margin="normal"
            variant="outlined"
            fullWidth
            required
          />
          <TextField
            id="outlined-email-input"
            label="Email"
            type="email"
            name="email"
            autoComplete="email"
            margin="normal"
            variant="outlined"
            fullWidth
            required
          />
          <TextField
            id="outlined-email-input"
            label="Feedback"
            multiline
            rows="4"
            type="text"
            name="message"
            autoComplete="feedback"
            margin="normal"
            variant="outlined"
            fullWidth
            required
          />
          {isSending ? (
            <LinearProgress
              style={{
                width: "100%",
                transform: "translateY(-12px)",
                borderRadius: "2px"
              }}
            />
          ) : null}
          <Button
            style={{ marginTop: "10px" }}
            variant="outlined"
            color="primary"
            type="submit"
            disabled={isSending ? true : false}
          >
            {/* if sending is true then show circular progress */}
            {isSending ? "Sending Feedback" : "Send Feedback"}
          </Button>
        </Grid>
      </div>

      <div className="deliver"></div>

      <div className="email-feedback-method">
      <h4>Reply via mail to nDs address(Phản hồi qua mail tới địa chỉ của nDs)</h4>
      <div className="form-feedback">
        <Grid
          component="form"
          ref={formElEmail}
          onSubmit={submitFormEmail}
          container
          required
          justify="center"
          style={{ width: "100%", margin: "0 auto", padding: '1rem' }}
        >
          <ReCaptcha
            sitekey="6Le1toEUAAAAAITyNwqEMaz3hFAYzciSJDMomrgN"
            // action="action_email"
            verifyCallback={verifyCallback}
          />
          <TextField
            id="outlined-email-input"
            label="Email"
            type="email"
            name="email"
            autoComplete="email"
            margin="normal"
            variant="outlined"
            fullWidth
            required
          />
          <TextField
            id="outlined-email-input"
            label="Feedback"
            multiline
            rows="4"
            type="text"
            name="message"
            autoComplete="feedback"
            margin="normal"
            variant="outlined"
            fullWidth
            required
          />
          {isSending ? (
            <LinearProgress
              style={{
                width: "100%",
                transform: "translateY(-12px)",
                borderRadius: "2px"
              }}
            />
          ) : null}
          <Button
            style={{ marginTop: "10px" }}
            variant="outlined"
            color="primary"
            type="submit"
            disabled={isSending ? true : false}
          >
            {/* if sending is true then show circular progress */}
            {isSending ? "Sending Feedback" : "Send Feedback"}
          </Button>
        </Grid>
      </div>       
      </div>
     
    </div>
  );
};

export default FeedbackApp;
