import React, { Component } from 'react'
import defaultImg from '../images/Logomini.svg';
import className from 'classnames';
import { Link } from 'react-router-dom';
import { OrderedMap } from 'immutable';
import _ from 'lodash';
import {ObjectID} from "../helpers/objectid";
import { IconButton } from '@material-ui/core';
import { MdSettings } from 'react-icons/md';
import moment from 'moment';
// import { UserLists } from "./searchUser";



export default class App3 extends Component {
    constructor(props){
        super(props);
        this.state = {
            height: window.innerHeight,
            newMessage: '',
            searchUser: '',
            showSearchUser : false,
        }

    }

    renderChannelTitle = (channel = {}) => {
        const { store } = this.props;
        const members = store.getMembersFromChannel(channel);
         const names = [];

         members.forEach((user) => {
             const name = _.get(user, 'name')
             names.push(name);
         });
        return <h4>{_.join(names, ',')}</h4>
    }

    handleOnClick = (user) => {
        console.log("hsdcysvsv",user.name)
        const { store } =this.props;
        const userId = _.get(user, '_id');
        const channelId = _.get( store.getActiveChannel(),'_id')
        store.addUserToChannel(channelId, userId);
    }
    _onCreateChannel = () =>{
        const { store } =this.props;
        const channelId = new ObjectID().toString();
        const channel = {
            _id: channelId,
            title: 'New Mess',
            lastMessage: "",
            members: new OrderedMap(),
            messages: new OrderedMap(),
            isNew: true,
            created: new Date(),
        };
        store.onCreateNewChannel(channel);
    }
    scrollMessagesToBottom = () => {
        if(this.messagesRef){
            this.messagesRef.scrollTop = this.messagesRef.scrollHeight;
        }
    }
    renderMessage(message){
        const txt = _.get(message, 'body' , '');
        const html = _.split(txt, '\n').map((m, key) => {
            return <p key={key} dangerouslySetInnerHTML={{ __html: m }}></p>
        })
        return html;
    }
    handleSubmit = (e) => {

        const { newMessage } = this.state;
        const { store } = this.props;
        e.preventDefault();

        if(_.trim(newMessage).length){
            const messageId = new ObjectID().toString();
            const channel = store.getActiveChannel();
            const channelId = _.get(channel , '_id', null);
            const currentUser = store.getCurrentUser();
            const message = {
                _id: messageId,
                channelId: channelId,
                body : newMessage,
                author : _.get(currentUser, 'name'),
                avatar: defaultImg,
                me: true,
            };
            
            store.addMessage(messageId, message);
            this.setState({newMessage: ""});
        }
        this.setState({newMessage: ""});

    };
    _onResi = () => {
        this.setState({height: window.innerHeight});
    }
    // addTestMessages = () => {
    //     const {store} = this.props

      
    //     for(let i = 0; i < 20; i++){
    //         let isMe = false;
    //         if(i % 2 === 0) {
    //             isMe = true;
    //         }
    //         const newMsg = {
    //             _id: `${i}`,
    //             author: `Author: ${i}`,
    //             body: `The body of message ${i}`,
    //             avatar: defaultImg,
    //             me: isMe,
    //             timing: '0:00'
    //         }
    //         store.addMessage(i, newMsg);

    //     }

    //     for (let c = 0; c<10; c++){
    //         const newChannel ={
    //             _id: `${c}`,
    //             title: `Channel title ${c}`,
    //             lastMessage: `Hey there is.. ${c}`,
    //             members: new OrderedMap({
    //                 '1': true,
    //                 '2': true,
    //             }),
    //             messages: new OrderedMap(),
    //             created: new Date(),
    //         }
    //         const moreMsgId =`${c + 1}`;
    //         const msgId = `${c}`;
    //         newChannel.messages = newChannel.messages.set(msgId, true);
    //         newChannel.messages = newChannel.messages.set(moreMsgId, true);
    //         store.addChannel(c, newChannel);
    //     }

    // }
    componentDidUpdate(){
        this.scrollMessagesToBottom();
        console.log("CDidUpdate");
    }
    componentDidMount(){
        window.addEventListener('resize', this._onResi);
        this.addTestMessages();
        console.log("CDidMount");
    }
    componentWillUnmount(){
        window.removeEventListener('resize', this._onResi);
        console.log("CWillUnMount");
    }
    
    render() {

        const { height, newMessage, searchUser } = this.state;
        const { store } = this.props;
        const activeChannel = store.getActiveChannel();
        const messages = store.getMessagesFromChannel(activeChannel);
        const members = store.getMembersFromChannel(activeChannel);
        const channels = store.getChannels();
        const usersList = store.searchUsers(searchUser);
        const style = {
            height: height,
        }
        const resultSearch = () => {
                return (
                    <div className="search-user">
                        <div className="user-list">
                        {usersList.map((user, index) =>{
                            return <div onClick={() => this.handleOnClick(user)} key={index} className="user">
                            <img src={user.avatar} alt={user.name} />
                            <span>{user.name}</span>
                        </div>
                        })
                        } 
                        </div>
                    </div>
                )
        }
        return (
            <div style={style} className="app3">
               <div className="chat-header">
                    <div className="header-left">
                        <div className="action">
                            <Link onClick={ this._onCreateChannel} className="btn-primary">AddNew</Link>
                        </div>
                    </div>

                    {_.get(activeChannel, 'isNew') ? <div className="toolbar">
                        <form className="tool">
                            <textarea
                            type="text"
                            value ={ this.state.searchUser }
                            placeholder="To UserName/Email"
                            onChange = {
                                ({target})=>{
                                    this.setState({searchUser:target.value})
                                }
                            }
                            />
                        </form>
                        {resultSearch()}
                        </div> : null }

                    <div className="header-mid">
                        <h3>{_.get(activeChannel,'title', '')}</h3>
                    </div>
                    <div className="header-right">
                        <div className="user-bar">
                            <div className="name-user">nDs</div>
                            <img src={defaultImg} alt="user-img" className="img-user"></img>
                        </div>
                        <div className="toolsRight-bar">
                        <IconButton  style={{ color: "rgba(0, 0, 0, 0.5)"}}>
                            <MdSettings />
                        </IconButton>
                        </div>
                    </div>
                </div>
                <div className="chat-main">
                    <div className="sidebar-left">
                        <div className="chanels">
                            {channels.map((channel, index) =>{
                                return (
                                    <div onClick= {(index) => {
                                        store.setActiveChannelId(channel._id);
                                    }} key={channel._id} className={className('chanel', {'active' : _.get(activeChannel, '_id') === _.get(channel, '_id', null)})}>
                                        <div className="user-img">
                                            <img src={defaultImg} alt="user-img" />
                                        </div>
                                        <div className="chanel-info">
                                            {this.renderChannelTitle(channel)}
                                            <p>{channel.lastMessage}</p>
                                        </div>
                                    </div>
                                )
                            })}
                            
                        </div>
                    </div>
                    <div className="chat-content">
                        <div ref={(ref) => this.messagesRef = ref} className="messages">
                            { 
                                messages.map((mess, index) => { 
                                    return (
                                        <div key={index} className={`${mess.me ? 'message-me' : 'message'}`}>
                                        <img src={mess.avatar} alt="user-img" className="img-user"></img>
                                        <div className="message-body">
                                            <div className="username">{`${mess.me ? 'Me' : mess.author}`} say:</div>
                                            <p className="message-text">
                                                { this.renderMessage(mess) }
                                                <p className="timing">{mess.timing}</p>
                                            </p>
                                        </div>
                                        </div>
                                    );
                                })
                            }
                           
                            {/*  <div className="message-me">
                                 <img src={defaultImg} alt="user-img" className="img-user"></img>
                                 <div className="message-body">
                                     <div className="username">Me say:</div>
                                     <p className="message-text">
                                         Hello you come app chat!
                                         <p className="timing">0:00</p>
                                     </p>
                                   
                                 </div>
                             </div> */}
                        </div>
                        <div className="message-input">
                            <form 
                                onSubmit= { this.handleSubmit }
                                disabled = { newMessage.length < 1 }
                                className="message-form">
                                <textarea  
                                    id = "message"
                                    ref={(input)=> this.input = input}
                                    type = "text"
                                    className = "form-control"
                                    value = { newMessage }
                                    autoComplete = {'off'}
                                    placeholder = "Type something interesting"
                                    onKeyUp = { (e) => {  
                                        if(e.keyCode === 13 && !e.shiftKey) 
                                        { this.handleSubmit(e); }}}
                                    onChange = {
                                    	({target})=>{
                                    		this.setState({newMessage:target.value})
                                    	}
                                    }
                                    />
                               
                                <button
                                    disabled = { newMessage.length < 1 }
                                    type = "submit"
                                    className = "send"
                                    onClick = { this.handleSubmit }
                                > Send </button>
                            </form>
			            </div>
                    </div>
                   
                    <div className="sidebar-right">
                        <div className="title-right">Members</div>
                        <div className="chanels">
                            { members.map((member, index) => {
                                return (
                                    <div key ={ index } className="chanel">
                                        <div className="user-img">
                                            <img src={_.get(member, 'avatar')} alt="user-img" />
                                        </div>
                                        <div className="chanel-info">
                                            <h4>{ member.name }</h4>
                                            <p>Joined: 2000 years ago</p>
                                        </div>
                                    </div>
                                )
                            })}
                            
                        </div>
                        
                    </div>

                </div>  
            </div>
        )
    }
}
