import React from 'react'
import { MdChat, MdDonutLarge, MdMoreVert } from 'react-icons/md';
import { Avatar, IconButton, InputBase } from "@material-ui/core";
import { Search, ChatSharp } from "@material-ui/icons";
import SidebarChats from './SidebarChats';
import { useAuth0 } from '@auth0/auth0-react';
import { LOGOUT } from '../Events';


function Sidebar() {

    const { user, isAuthenticated } = useAuth0();
    const isUser = isAuthenticated && user;

    return (
        <div className="sidebar">
            <div className="sidebar_header">
                {isUser && user.picture &&  <img src={user.picture} alt={user.name} />}
                <div className="sidebar_headerRight">
                    
                    <IconButton>
                        <MdDonutLarge />
                    </IconButton>
                    <IconButton>
                        <MdChat />  
                    </IconButton>
                    <IconButton>
                        <MdMoreVert />  
                    </IconButton>

                </div>
            </div>
            <div className="sidebar_search">
                <form style={{ width: "100%", display: "flex", background: "var(--clr-primary-8)", borderRadius: ".5rem" }} >
                    <Search style={{ alignItems: "center", marginTop: ".4rem", marginLeft: ".5rem" }}/>
                    <InputBase
                    fullWidth
                    placeholder="Search..."
                    autoFocus
                    style={{ color: "#303030", paddingLeft: "8px", marginTop: ".3rem" }}
                   
                    />
                </form>
            </div>
            <div className="sidebar_chats">
                <div className="sidebarChat" ref='user' onClick= {
                    (e) => { (e.target === this.ref.user) && setActiveChat(null)}
                }>
                    {
                        ChatSharp.map((chat) => {
                            if(chat.name) {
                                const lastMessage = chat.messages[chat.messages.lenght - 1];
                                const user = chat.users.find(({name}) => {
                                    return name !== this.props.name
                                }) || { name: "Community" }
                                const classNames = (activeChat && activeChat.id === chat.id) ? 'active' : ''

                                return (
    
                                    <div className="sidebarChat_info" onClick= {
                                        () => { setActiveChat(chat) }
                                    }>
                                        <img className="userImg">{user.name[0].toUpperCase()}</img>
                                        <h4>{user.name}</h4>
                                        <p>{lastMessage && <div className="lastMessage">{lastMessage.messages}</div>}</p>
                                    </div>
                                )
                            }
                            return null
                        })
                    }
                    
                </div>
            </div>
            <div className="sidebar_footer">
                <span>{user.name}</span>
                <div onClick= {() => {logout()}} className="btn-logout">
                    <IconButton>
                        <MdMoreVert />  
                    </IconButton>
                </div>
            </div>
            
        </div>
    )
}

export default Sidebar
