import React, { Component } from 'react'
import { MdChat, MdDonutLarge, MdMoreVert } from 'react-icons/md';
import { Avatar, IconButton, InputBase } from "@material-ui/core";
import { Search, ChatSharp } from "@material-ui/icons";
import { get, last, differenceBy } from 'lodash';
import { SidebarUsers } from './SidebarUsers';
import { createChatNameFromUsers } from '../Factories';
// import SidebarChats from './SidebarChats';

export default class Sidebar extends Component {
	static type = {
		CHATS: "chats",
		USERS: "users"
	}
   constructor(props){
	   super(props)
	   this.state = {
		   reciever: "",
		   activeSidebar: Sidebar.type.CHATS
	   }
   }
    handleSubmit = (e) => {
		e.preventDefault()
		const { reciever } = this.state
		const { onSendPrivateMessage } = this.props
		onSendPrivateMessage(reciever)
		this.setState({reciever:""})
	}
	addChatForUser = (username) => {
		this.setActiveSidebar(Sidebar.type.CHATS)
		this.props.onSendPrivateMessage ( username )
	}
	setActiveSidebar= (newSideBar) => {
		this.setState({ activeSidebar:newSideBar })
	}
    render(){
        const { chats, activeChat, user, setActiveChat, logout, users } = this.props
		const { reciever, activeSidebar } = this.state
        return (
        	<div className="sidebar">
				<div className="sidebar_header">
                 {/* {user && user.picture &&  <img src={user.picture} alt={user.name} />} */}
                <div className="sidebar_headerRight">
                    
                    <IconButton>
                        <MdDonutLarge />
                    </IconButton>
                    <IconButton>
                        <MdChat />  
                    </IconButton>
                    <IconButton>
                        <MdMoreVert />  
                    </IconButton>

                </div>
            	</div>
				<div className="sidebar_search">
				<form
				onSubmit={this.handleSubmit}
				 style={{ width: "100%", display: "flex", background: "var(--clr-primary-8)", borderRadius: ".5rem" }} >
                    <Search style={{ alignItems: "center", marginTop: ".4rem", marginLeft: ".5rem" }}/>
                    <InputBase
                    fullWidth
                    placeholder="Search..."
                    autoFocus
					style={{ color: "#303030", paddingLeft: "8px", marginTop: ".3rem" }}
					value={reciever}
					onChange={(e) => {this.setState({reciever:e.target.value})}}
                    />
                </form>
            	</div>
				<div className="sidebar_select">
					<div
					onClick={ ()=>{ this.setActiveSidebar(Sidebar.type.CHATS)}}
					 className={`sidebar_option ${(activeSidebar === Sidebar.type.CHATS) ? 'active' : ''} `}>
						 <span>Chats</span>
					</div>
					<div
					onClick={ ()=>{ this.setActiveSidebar(Sidebar.type.USERS)}} 
					className={`sidebar_option ${(activeSidebar === Sidebar.type.USERS) ? 'active' : ''}`}>
						<span>Users</span>
					</div>
				</div>
				<div className="sidebar_chats">
				<div 
						className="users" 
						ref='users' 
						onClick={(e)=>{ (e.target === this.refs.user) && setActiveChat(null) }}>
						
						{
						activeSidebar === Sidebar.type.CHATS ?
						chats.map((chat)=>{
							if(chat.name){
								
								return(
									<SidebarUsers
									key={ chat.id }
									name = {chat.isCommunity ? chat.name : createChatNameFromUsers(chat.users, user.name) }
									lastMessage = { get(last(chat.messages), 'message', '')}
									active ={ activeChat.id === chat.id}
									onClick= { () => {
										this.props.setActiveChat(chat)
									}}
									>
									</SidebarUsers>
								// <div className="sidebarChat" key={chat.id} onClick={ ()=>{ setActiveChat(chat) } }>
								// 	<Avatar>
								// 		{chatUserName[0].toUpperCase()}
								// 	</Avatar>
									
								// 	<div className="sidebarChat_info">
								// 		<h4 className="name">{chatUserName}</h4>
								// 		{lastMessage ? <p className="last-message">{lastMessage.message}</p> : "none.."}
								// 	</div>
								// </div>
							)
							}

							return null
						})	
						:
							differenceBy(users, [user], 'name').map((otherUser) => {
								return (
									<SidebarUsers
									key = { otherUser.id }
									name = { otherUser.name}
									onClick = {() => { this.addChatForUser( otherUser.name)}}
									/>
								)
							})
						
					}

						
					</div>
				</div>
				
					<div className="current-user">
						<span>{user.name}</span>
						<div onClick={()=>{logout()}} title="Logout" className="logout">
							<MdMoreVert />	
						</div>
					</div>
			</div>
           
           
		)
    }
}
