import React, { Component } from 'react';
import './Animation.css'
class Snow extends Component {
  constructor(props) {
    super(props);
    this.canvas = React.createRef();
  }
  componentDidUpdate() {
    this.snow();
  }

  snow(){
    const canvas = this.canvas.current;
  
    const height = canvas.height;
    const width = canvas.width;
    
    window.addEventListener("resize",function(){
        canvas.height = window.innerHeight;
        canvas.width = window.innerWidth;
    });
    
    const ctx = canvas.getContext('2d');
    
    
    const flakes = [];
    class snowfall{
    
        static snowFall(){
            ctx.clearRect(0, 0, width, height);
            snowfall.addFlakes();
            snowfall.addSnow();
        };
    
        static addFlakes(){
            const x = Math.ceil(Math.random()* width);
            const speed = Math.ceil(Math.random()* 5);
            const radius = 10* Math.PI;
    
            flakes.push({
                x:x,
                y:0,
                speed:speed,
                radius:radius
            });
        };
    
        static addSnow(){
            for(let i=0; i<flakes.length; i++){
                let oneFlake = flakes[i];
    
                // creating circles
                ctx.beginPath();
                // color the circles
                ctx.fillStyle = "rgba(0, 0, 0, 0.8)";
                // drawing circle
                ctx.arc(oneFlake.x, oneFlake.y += oneFlake.speed/2, oneFlake.speed * 0.8, 0, oneFlake.radius);
                ctx.fill();
            }
        };
    }
    
    setInterval(()=> snowfall.snowFall(),10);
  }

  
 
  render() {
    
    return <canvas width="1250px" height="800px" ref={this.canvas}>
    </canvas>;
  }
}

export default Snow;
