import React from 'react'
import styled from 'styled-components';
import { GoRepo, GoGist } from 'react-icons/go';
import { FiUsers, FiUserPlus } from 'react-icons/fi';

const Detail1 = (props) => {
    const weathers = props.weather;
    const descript = props.descript;
    const coording = props.coording;

    const { temp, humidity, pressure} = weathers;
    const { lat, lon } = coording;
    const { description } = descript;
    const items = [
        {
        id: 2,
          icon:<GoRepo className="icon" />,
          label:'temp',
          value: (temp-273.15).toFixed(2),
          color: 'pink',
      },
      {
        id: 3,
          icon:<FiUsers className="icon" />,
          label:'humidity',
          value: humidity,
          color: 'green',
      },
      {
        id: 4,
          icon:<FiUserPlus className="icon" />,
          label:'pressure',
          value: pressure,
          color: 'purple',
      },
      {
        id: 1,
          icon:<GoGist className="icon" />,
          label:'description',
          value: description,
          color: 'yellow',
      },
      ];
    return (
        <section className='section'>
        <h4>Weather forecast for<span className="weather-city">VietNam</span></h4>
        <p>Coordinates: <span className="coord-city">{lat}:{lon}</span></p>
        <Wrapper>
            {items.map((item) => {
            return <Item key={item.id} {...item}></Item>;
            })}
        </Wrapper>
        </section>
  );
};

const Item = ({icon, label, value, color}) => {
    return <article className="item">
      <span className={color}>{icon}</span>
        <p>{value}</p>
        <p>{label}</p>
    </article>
  }

const Wrapper = styled.section`
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
    gap: 1rem 2rem;
    /* @media (min-width: 640px) {
    grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
    } */
.item {
    border-radius: var(--radius);
    padding: 1rem 2rem;
    background: var(--clr-white);
    display: grid;
    grid-template-columns: auto 1fr;
    column-gap: 1rem;
    align-items: center;
    span {
        width: 3rem;
        height: 3rem;
        display: grid;
        place-items: center;
        border-radius: 50%;
    }
    .icon {
        font-size: 1.5rem;
    }
    h3 {
        color: rgba(0, 0, 0, 0.5);
        margin-bottom: 0;
        letter-spacing: 0;
    }
    p {
        color: rgba(0, 0, 0, 0.5);
        margin-bottom: 0;
        text-transform: capitalize;
    }
    .pink {
        background: #ffe0f0;
        color: #da4a91;
    }
    .green {
        background: var(--clr-primary-10);
        color: var(--clr-primary-5);
    }
    .purple {
        background: #e6e6ff;
        color: #5d55fa;
    }
    .yellow {
        background: #fffbea;
        color: #f0b429;
    }
    }
`;
export default Detail1
