import { OrderedMap } from 'immutable';

const users = OrderedMap({
    1: {_id:1, name:"nDs", creaded: new Date()},
    2: {_id:2, name:"nDs0", creaded: new Date()},
    3: {_id:3, name:"nDs1", creaded: new Date()},
})
export default class Store {
    constructor(appComponent){
        this.app = appComponent;
        this.messages = new OrderedMap();
        this.channels = new OrderedMap();
        this.activeChannelId = null;
        this.user = {
            _id: 0,
            name: 'nDs',
            creaded: new Date(),
        }
    }

    setActiveChannelId(id) {
        this.activeChannelId = id;
        this.update();
    }
    getActiveChannel(){
        const channel = this.activeChannelId ? this.channels.get(this.activeChannelId) : this.channels.first();
        return channel;
    }

    addMessage(index, message = {}){
        this.messages = this.messages.set(index, message);
        this.update();
    }

    getMessages(){
        return this.messages.valueSeq();
    }
    getMessagesFromChannel(channel){
        if(channel){
            return channel.messages.keySeq();
        }
        return new OrderedMap();
        
    }

    addChannel(index, channel = {}){
        this.channels = this.channels.set(index, channel);
        this.update();
    }

    getChannels(){
        return this.channels.valueSeq();
    }
    update(){
        this.app.forceUpdate();
    }
}
