import React, { Component } from 'react'

export class MainVideo extends Component {
    constructor(props){
        super(props)
        this.localVideoref = React.createRef();
        this.remoteVideoref = React.createRef();
    }
    render() {
        const constraints = { audio: false, video: true }
        const success = (stream) => {
            this.localVideoref.current.srcObject = stream;
        }
        const failure = (e) => {
            //
        }
        navigator.mediaDevices.getUserMedia( constraints )
        .then( success )
        .catch( failure )
        return (
            <div>
                <video ref={this.localVideoref}
                style={{width: 240, height: 240, margin: 5, backgroundColor: 'black'}}
                autoPlay ></video>
                
                <video ref={this.remoteVideoref}
                style={{width: 240, height: 240, margin: 5, backgroundColor: 'black'}}
                autoPlay ></video>

                <button onClick={this.createOffer}>Offer</button>
                <button onClick={this.createAnswer}>Answer</button>
                <br/>
                <textarea style={{height:'2rem', width: '23rem'}} ref={ref => { this.textref = ref}}/>
                <br/>
                <button onClick={this.setRemoteDescription}>Set Remoete Desc</button>
                <button onClick={this.addCandidate}>Add Candidate</button>
            </div>
        )
    }
}
export default MainVideo
