import React, { useContext, useEffect, useState } from "react";
// import { GlobalContext } from "./GlobalState";
import { Link } from "react-router-dom";
import { Button, Grid, Typography } from "@material-ui/core";
import { NavigateNext } from "@material-ui/icons";
// import bgImg from "../images/music-bg.svg";
// import gIcon from "../images/google.svg";
// import bgImg2 from "../images/defaultBg2.jpg";
import GoogleSignIn from "./GoogleSignIn";
// import App2 from "./App2";

// const bgStyle = {
//   background: `url(${bgImg2}) no-repeat`,
//   backgroundPositionX: "50%",
//   width: "100%",
//   height: "100%"
// };



const LoginPage = ({ continueToHome }) => {
  const showSignIn = () => {
    // if user has already closed the popup dont show it
    if (localStorage.getItem("signInClosed") !== "true") {
      return <GoogleSignIn />;
    }
  };

  return (
    <>
      <Banner title="Welcome to nDsApp" subtitle="
Interesting Experience Right Now">
	 		<Link to="/page1/home" className="btn-primary" onClick={continueToHome}>
        Continue
       <NavigateNext />
	 		</Link>
	 	 	</Banner>
    </>
  );
};
 
export default LoginPage;
