import React, { Component } from 'react';
import defaultImg from '../images/Logomini.svg';
import _ from 'lodash';



export class UserBar extends Component {
    constructor(){
        super()
        this.state = {
            user: {
                email: '',
                password: '',
                nickname: ''
            }
        }
    }

    onSubmit = (e) => {
        const { store } = this.state;
        const { user } = this.state;
        e.preventDefault();
        if (user.email && user.password){
            store.login(user.email, user.password).then((user) => {
                console.log(user);
            }).catch((err) => {
                console.log(err);
            });
        }
    }

    onTxtfieldChange = (e) => {
        let { user } = this.state;
        const field = e.target.name;
        user[field] = e.target.value;
        this.setState({
            user: user
        });
    }

    render() {
        const { user } = this.state;
        const { store } = this.props;
        const me = store.getCurrentUser();
        const profileImg = _.get(me, 'avatar');
        return (
    <>
        <div className="user-bar">
            { !me ? <button type="button" className="login-btn">Sign In</button> : null}
            <div className="name-user">{_.get(me, 'name')}</div>
            <img src={profileImg ? profileImg : defaultImg} alt="user-img" className="img-user"></img>
            <div className="user-form">
            <form onSubmit= {this.onSubmit}>
            <ul className="form-container">
                <li>
                    <h4>Sign In / Register Your Account</h4>
                </li>
                <li>
                    <label htmlFor="email">
                     Email
                    </label>
                    <input onChange={(e) => this.onTxtfieldChange(e) } 
                    placeholder="example.vn@gmail.com" 
                    required type="email" name="email"
                    value = {_.get(user, 'email')} 
                    id="email" ></input>
                </li>
                <li>
                    <label htmlFor="password">Password</label>
                    <input onChange={(e) => this.onTxtfieldChange(e) }
                    autoComplete = {'off'} 
                    placeholder="Your-password" 
                    required type="password"
                    value = {_.get(user, 'password')} 
                    id="password" name="password" ></input>
                </li>
                <div className="footer-login">
                    <div className="img">
                        <img src={defaultImg} alt="nds"/>
                    </div>
                    <div className="btn-cc">
                    <li>
                    <button type="submit" className="login-btn">Signin</button>
                </li>
                <li>
                    <button className="login-btn" >Create new account?</button>
                </li>
                    </div>
                </div>
            </ul>
            </form>
            </div>
        </div>
    </>
    )
    }
}

export default UserBar
