import React, { useContext, useEffect, useState } from "react";
import { GlobalContext } from "./GlobalState";
import { Link } from "react-router-dom";
import { Button, Grid, Typography } from "@material-ui/core";
import { NavigateNext } from "@material-ui/icons";
import bgImg from "../images/music-bg.svg";
import gIcon from "../images/google.svg";
import bgImg2 from "../images/defaultBg2.jpg";
import GoogleSignIn from "./GoogleSignIn";
// import App2 from "./App2";

const bgStyle = {
  background: `url(${bgImg2}) no-repeat`,
  backgroundPositionX: "50%",
  width: "100%",
  height: "100%"
};



const LoginPage = ({ continueToHome }) => {
  const showSignIn = () => {
    // if user has already closed the popup dont show it
    if (localStorage.getItem("signInClosed") !== "true") {
      return <GoogleSignIn />;
    }
  };

  return (
    <Grid
      style={{ height: "80vh" }}
      container
      direction="column"
      display="inline-block"
      justify="space-around"
      alignItems="center"
      background ="`url(${bgImg2}) no-repeat`"
    >
      {showSignIn()}

      <div style={bgStyle} />

      <Typography
        variant="h6"

        align="center"
        style={{ padding: "10px" }}
      >
        Listen to unlimited songs without any ads for free only on nDs Music
      </Typography>
      {/* <img
        style={{
          width: "70vw",
          maxWidth: "350px"
        }}
        src={handcraftedText}
        alt="Handcrafted by Shivam"
      /> */}
      <Link to={"/page1/home"}>
      <Button variant="outlined" onClick={continueToHome}>
        Continue
        <NavigateNext />
      </Button>
      </Link>
      
    </Grid>
  );
};

export default LoginPage;
