import React, { Component } from 'react'
import { MdChat, MdDonutLarge, MdMoreVert } from 'react-icons/md';
import { Avatar, IconButton, InputBase } from "@material-ui/core";
import { Search, ChatSharp } from "@material-ui/icons";
// import SidebarChats from './SidebarChats';

export default class Sidebar extends Component {
	static type = {
		CHATS: "chats",
		USERS: "users"
	}
   constructor(props){
	   super(props)
	   this.state = {
		   reciever: "",
		   activeSidebar: Sidebar.type.CHATS
	   }
   }
    handleSubmit = (e) => {
		e.preventDefault()
		const { reciever } = this.state
		const { onSendPrivateMessage } = this.props
		onSendPrivateMessage(reciever)
		this.setState({reciever:""})
	}
	addChatForUser = (username) => {
		this.setActiveSidebar(Sidebar.type.CHATS)
		this.props.onSendPrivateMessage ( username )
	}
	setActiveSidebar= (newSideBar) => {
		this.setState({ activeSidebar:newSideBar })
	}
    render(){
        const { chats, activeChat, user, setActiveChat, logout, users } = this.props
		const { reciever, activeSidebar } = this.state
        return (
        	<div className="sidebar">
				<div className="sidebar_header">
                 {/* {user && user.picture &&  <img src={user.picture} alt={user.name} />} */}
                <div className="sidebar_headerRight">
                    
                    <IconButton>
                        <MdDonutLarge />
                    </IconButton>
                    <IconButton>
                        <MdChat />  
                    </IconButton>
                    <IconButton>
                        <MdMoreVert />  
                    </IconButton>

                </div>
            	</div>
				<div className="sidebar_search">
				<form
				onSubmit={this.handleSubmit}
				 style={{ width: "100%", display: "flex", background: "var(--clr-primary-8)", borderRadius: ".5rem" }} >
                    <Search style={{ alignItems: "center", marginTop: ".4rem", marginLeft: ".5rem" }}/>
                    <InputBase
                    fullWidth
                    placeholder="Search..."
                    autoFocus
					style={{ color: "#303030", paddingLeft: "8px", marginTop: ".3rem" }}
					value={reciever}
					onChange={(e) => {this.setState({reciever:e.target.value})}}
                    />
                </form>
            	</div>
				<div className="sidebar_chats">
				<div 
						className="users" 
						ref='users' 
						onClick={(e)=>{ (e.target === this.refs.user) && setActiveChat(null) }}>
						
						{
						chats.map((chat)=>{
							if(chat.name){
								const lastMessage = chat.messages[chat.messages.length - 1];
								const user = chat.users.find(({name})=>{
									return name !== this.props.name
								}) || { name:"Community" }
								const classNames = (activeChat && activeChat.id === chat.id) ? 'active' : ''
								
								return(
								<div 
									key={chat.id} 
									className={`user ${classNames}`}
									onClick={ ()=>{ setActiveChat(chat) } }
									>
									<div className="user-photo">{user.name[0].toUpperCase()}</div>
									<div className="user-info">
										<div className="name">{user.name}</div>
										{lastMessage && <div className="last-message">{lastMessage.message}</div>}
									</div>
									
								</div>
							)}
						
					})
				}

						
					</div>
				</div>
				
					<div className="current-user">
						<span>{user.name}</span>
						<div onClick={()=>{logout()}} title="Logout" className="logout">
							<MdMoreVert />	
						</div>
					</div>
			</div>
           
           
		)
    }
}
