import React, { Component } from 'react';
import defaultImg from '../images/svgnds.png';
import _ from 'lodash';
import classNames from 'classnames';
import { IconButton } from '@material-ui/core';
import { MdEdit, MdCheck } from 'react-icons/md';
import { RiImageEditFill } from 'react-icons/ri';
import Dropzone from "react-dropzone";
// import axios from "axios";
import crypto from 'crypto';
import CryptoJS from 'crypto-js';
import fetchJsonp from "fetch-jsonp";



const GeoAPI = "https://ipapi.co/jsonp";

const keySecret = '3zTvzr3p67VC61jmV54rIYu1545x4TlY';
const algorithm = 'aes-256-ctr';
const keySecret2 = 'vOVH6sdmpNWjRRIqCc7rdxs01lwHzfr3';
const iv = crypto.randomBytes(16);


export class UserBar extends Component {
    constructor(){
        super()
        this.state = {
            message: null,
            showUserFrom : false,
            isLogin: true,
            edit: '',
            type:'text',
            editAcc: false,
            userId: '',
            user: {
                email: '',
                password: '',
                repassword: '',
                name: '',
                ipClient: null,
                birthday: '',
                country: '',
                phone: '',
                ID: '',
            }
        }
       
    }

    handleCreateR = () => {
       
        const userId = this.generateId(12);
        let { user } = this.state;
        const field = 'ID';
        user[field] = userId;
        this.setState({ user: user})
        console.log('ID of user random: ', userId);
      }
    dec2hex = (dec) => {
        return dec < 10
        ? '0' + _.toString(dec)
        : dec.toString(16)
    }
    generateId = (len) => {
        var arr = new Uint8Array((len || 40) / 2)
        window.crypto.getRandomValues(arr)
        return Array.from(arr, this.dec2hex).join('')
    }

    changeControl = (e) => {
        e.preventDefault();
        this.handleCreateR();
        let {isLogin} = this.state;
        this.setState({
            isLogin: !isLogin,
        })
      
    }

    onDrop = (files) => {
        const {store} = this.props;
        // console.log("file goc: ",files);
        let formData = new FormData();
        formData.append("myImage", files[0]);
        store.uploadUserAvatar(formData);
        // console.log("file format: ",formData);
    }
    handleEditNow = (e) => {
        e.preventDefault();
        const { store } = this.props;
        const { user, edit } = this.state;
        store.editUser(edit, user[edit])


        this.setState({
            edit: '',
            editAcc: false
        })
    }
    fetchIPclient = () => {
        try{
            fetchJsonp(`${GeoAPI}`)
            .then(function(response) {
                return response.json();
              })
              .then(json => {
                let { user } = this.state;
                const field = 'ipClient';
                user[field] = json;
                this.setState({ user: user})
              })
              .catch(function(ex) {
                console.log("parsing failed", ex);
              });
        }
        catch (err){ console.log(err) }

      };
    onClickOutSide = (e) => {
        if(this.ref && !this.ref.contains(e.target)){
            console.log("Here click out side login form!");
            this.setState({
                showUserFrom: false,
                showUserMenu: false
            })
        }
    }
    componentDidMount(){
        window.addEventListener('mousedown', this.onClickOutSide);
        this.fetchIPclient();
        // var password = this.encrypt('Hippo956592#');
        // const dehash = this.encrypt2(password);
        // console.log(dehash)
        // const rehash = this.decrypt2(dehash);
        // console.log("hash origin is : " , this.decrypt(rehash));

        // console.log('Didmount and fetch ipClient');
    }
    componentWillUnmount(){
        window.removeEventListener('mousedown', this.onClickOutSide);
        console.log('Unmount');
    }

    encrypt(text) {
        var salt = CryptoJS.lib.WordArray.random(128 / 8)
        var iv = CryptoJS.lib.WordArray.random(128 / 8)
        var encrypted = CryptoJS.AES.encrypt(text, CryptoJS.PBKDF2(keySecret, salt, { keySize: 256 / 32, iterations: 100 }) /* key */, { iv: iv, padding: CryptoJS.pad.Pkcs7, mode: CryptoJS.mode.CBC })
        var transitmessage = salt.toString() + iv.toString() + encrypted.toString();
        console.log(transitmessage);
        return transitmessage
    }

    decrypt(text) {
        var key = CryptoJS.PBKDF2(keySecret, CryptoJS.enc.Hex.parse(text.substr(0, 32)) /* Salt */, { keySize: 256 / 32, iterations: 100 })
        var decrypted = CryptoJS.AES.decrypt(text.substring(64) /* encrypted */, key, { iv: CryptoJS.enc.Hex.parse(text.substr(32, 32)) /* iv */, padding: CryptoJS.pad.Pkcs7, mode: CryptoJS.mode.CBC })
        return decrypted.toString(CryptoJS.enc.Utf8)
    }

    encrypt2 = (text) => {

        const cipher = crypto.createCipheriv(algorithm, keySecret2, iv);
    
        const encrypted = Buffer.concat([cipher.update(text), cipher.final()]);
    
        return {
            iv: iv.toString('hex'),
            content: encrypted.toString('hex')
        };
    };
    decrypt2 = (hash) => {

        const decipher = crypto.createDecipheriv(algorithm, keySecret2, Buffer.from(hash.iv, 'hex'));
    
        const decrpyted = Buffer.concat([decipher.update(Buffer.from(hash.content, 'hex')), decipher.final()]);
    
        return decrpyted.toString();
    };

    onSubmit = (e) => {

        const { store } = this.props;
        const { user , isLogin } = this.state;
        e.preventDefault();
        this.setState({
            message: null
        }, () => {
            if(isLogin){
                try{
                    store.login(user.email, user.password, user.ipClient.ip).then((user) => {

                        this.setState({
                            message: null,
                            showUserFrom: false,
                            showUserMenu: false
                        })
        
                    }).catch((err) => {
                        console.log(err);
                        this.setState({
                            message: {
                                body: err,
                                type: 'error'
                            }
                        })
                    });
                }
                catch (err) {
                    console.log(err);
                    this.setState({
                        message: {
                            body: 'An error occurred, please try again later!',
                            type: 'error'
                        }
                    })
                }
               
            } else {
                if(user.password === user.repassword){
                    store.register(user).then((_) => {
                        this.setState({
                            message: {
                                body: 'User created',
                                type: 'Success'
                            }
                        }, () => {
                            // now login this user
                            store.login(user.email, user.password).then((user) => { 
                            //
                            this.setState({
                                message: null,
                                showUserFrom: false,
                                showUserMenu: false
                            })
            
                        }).catch((err) => {
                            console.log(err);
                            this.setState({
                                message: {
                                    body: err,
                                    type: 'error'
                                }
                            })
                            });
                        })
                    }).catch((err) => {
                        console.log(err);
                        this.setState({
                            message: {
                                body: err,
                                type: 'error'
                            }
                        })
                    });
                }else{
                    console.log(user.repassword, 'and' , user.password);
                    this.setState({
                        message: {
                            body: 'Password not Comfirm',
                            type: 'error'
                        }
                    })
                }
                
            }
           
        })

    }

    onTxtfieldChange = (e) => {
        let { user } = this.state;
        const field = e.target.name;
        user[field] = e.target.value;
        this.setState({
            user: user
        });
    }
    onTxtfieldEChange = (e) => {
        let { user } = this.state;
        const field = e.target.name;
        user[field] = e.target.value;
        this.setState({
            user: user
        });
    }

    render() {
        const { user, message, isLogin, edit, type, editAcc } = this.state;
        const { store } = this.props;
        const me = store.getCurrentUser();
        const profileImg = _.get(me, 'avatar');
        const name = _.get(me,'name');
        const ID = _.get(me, 'ID');
        const email = _.get(me, 'email');
        const country = _.get(me, 'country');
        const phone = _.get(me, 'phone');
        const birthday = _.get(me, 'birthday');
        return (
    <>
        <div className="user-bar">
            { !me ? <button type="button" onClick={(e) => this.setState({showUserFrom: true})} className="login-btn">Sign In</button> : null}
            <div className="name-user">{_.get(me, 'name')}</div>
            <div className="img-user">
                <img className="img-profile" src={profileImg ? `http://localhost:8080/${profileImg}` : defaultImg} onClick={(e) => this.setState({showUserMenu: true})}  alt="user-img"></img>
            </div>

            { me && this.state.showUserMenu ?  <div className="user-form" ref = {(ref) => this.ref = ref}>
                <form>
                <ul className="form-container">
                    <li>
                        <h4>Your Account</h4>
                    </li>
                    {
                        editAcc ? <div>
                        <label style={{textTransform: 'capitalize'}} htmlFor={edit}>
                            {edit}
                        </label>
                        <div className="form-edit">
                            <div className="input-edit">
                            <input onChange={(e) => this.onTxtfieldEChange(e) } 
                            placeholder="type your change here"
                            required type={type} name={edit}
                            value = {_.get(user, {edit})} 
                            id={edit} ></input>
                            </div>
                        <div className="btn-edit">
                            <IconButton>
                                <MdCheck style={{ color: "rgba(23, 138, 8, 0.959)"}} onClick={this.handleEditNow} />
                            </IconButton>
                        </div>
                        </div>
                       
                    </div>
                    :
                    null
                    }
                    <li className ="header-menu">
                        <img src={`http://localhost:8080/${profileImg}`} alt="user-img" />
                        <div className="edit-avatar-icon">
                            <Dropzone onDrop={this.onDrop}>
                                {({getRootProps, getInputProps}) => (
                                    <section>
                                    <div {...getRootProps()}>
                                        <input {...getInputProps()} />
                                            <RiImageEditFill />
                                    </div>
                                    </section>
                                )}
                            </Dropzone>
                        </div>
                        <span>{name}</span>
                    </li>
                    <div className ="body-menu">
                        <p htmlFor="email">Your Name: {name}</p>
                        <div>
                        <IconButton  style={{ color: "rgba(0, 0, 0, 0.5)"}}>
                            <MdEdit onClick = {() => this.setState({editAcc: true, edit: 'name'})} />
                        </IconButton>
                        </div>
                    </div>
                    <div className ="body-menu">
                        <p htmlFor="email">Birthday: {birthday}</p>
                        <div> <IconButton  style={{ color: "rgba(0, 0, 0, 0.5)"}}>
                            <MdEdit  onClick = {() => this.setState({editAcc: true, edit: 'birthday', type: 'date'})} />
                        </IconButton></div>
                    </div>
                    <div className ="body-menu">
                        <p htmlFor="email">ID User: {ID}</p>
                        <div> <IconButton  style={{ color: "rgba(0, 0, 0, 0.5)"}}>
                            <MdEdit  onClick = {() =>alert("You can't edit your ID!")} />
                        </IconButton></div>
                    </div>
                    <div className ="body-menu">
                        <p htmlFor="email">Email Address: {email}</p>
                        <div> <IconButton  style={{ color: "rgba(0, 0, 0, 0.5)"}}>
                            <MdEdit  onClick = {() =>alert("You can't edit your email address!")} />
                        </IconButton></div>
                    </div>
                    <div className ="body-menu">
                        <p htmlFor="email">Contact Number: {phone}</p>
                        <div> <IconButton  style={{ color: "rgba(0, 0, 0, 0.5)"}}>
                            <MdEdit  onClick = {() => this.setState({editAcc: true, edit: 'phone', type: 'number'})} />
                        </IconButton></div>
                    </div>
                    <div className ="body-menu">
                        <p htmlFor="email">Country: {country}</p>
                        <div> <IconButton  style={{ color: "rgba(0, 0, 0, 0.5)"}}>
                            <MdEdit  onClick = {() => this.setState({editAcc: true, edit: 'country'})} />
                        </IconButton></div>
                    </div>
                  
                    
                   
                    <div className="footer-menu">
                        <div className="img">
                            <img style={{maxHeight: "5rem", maxWidth: "5rem"}} src={defaultImg} alt="nds"/>
                        </div>
                        <div className="btn-cc">
                        <li>
                        <button onClick = { () => store.signOut()}
                        type="button" className="login-btn">Signout</button>
                        </li>
                    {/* <li>
                        <button className="login-btn" >Create new account?</button>
                    </li> */}
                        </div>
                    </div>
                </ul>
                </form>
                </div>
                :
                null
            }
             { !me && this.state.showUserFrom ? <div className="user-form" ref = {(ref) => this.ref = ref}>
                <form onSubmit= {this.onSubmit}>
                <ul className="form-container">
                    <li>
                        <h4>Sign In / Register Your Account</h4>
                    </li>
                    <li>
                        { message ? <p className={classNames('app-msg', _.get(message, 'type'))}>
                            {_.get(message, 'body')}
                        </p> : null } 
                    </li>
                    {
                        !isLogin ? <li>
                        <label htmlFor="name">
                        Name
                        </label>
                        <input onChange={(e) => this.onTxtfieldChange(e) } 
                        placeholder="Your-Name" 
                        required type="text" name="name"
                        value = {_.get(user, 'name')} 
                        id="name" ></input>
                    </li>
                    :
                    null
                    }
                    
                    <li>
                        <label htmlFor="email">
                        Email
                        </label>
                        <input onChange={(e) => this.onTxtfieldChange(e) } 
                        placeholder="example.vn@nds.com" 
                        required type="email" name="email"
                        value = {_.get(user, 'email')} 
                        id="email" ></input>
                    </li>
                    <li>
                        <label htmlFor="password">Password</label>
                        <input onChange={(e) => this.onTxtfieldChange(e) }
                        autoComplete = {'off'} 
                        placeholder="Your-password" 
                        required type="password"
                        value = {_.get(user, 'password')} 
                        id="password" name="password" ></input>
                    </li>
                    {
                        !isLogin ? <li>
                        <label htmlFor="repassword">
                        Comfirm Password
                        </label>
                        <input onChange={(e) => this.onTxtfieldChange(e) } 
                        placeholder="Re-password" 
                        required type="password" name="repassword"
                        value = {_.get(user, 'repassword')} 
                        id="repassword" ></input>
                    </li>
                    :
                    null
                    }
                    <div className="footer-login">
                        <div className="img">
                            <img style={{maxHeight: "5rem", maxWidth: "5rem"}} src={defaultImg} alt="nds"/>
                        </div>
                        <div className="btn-cc">
                        <li>
                            <button type="submit" className="login-btn">{isLogin ? 'Log In' : 'Create Now'}</button>
                        </li>
                        <li>
                        <button type="button" onClick = {this.changeControl}
                        className="login-btn" > { isLogin ? 'Create New Account?' : 'Already have an account?' } </button>
                      </li>
                        </div>
                    </div>
                </ul>
                </form>
                </div>
                :
                null
        }
 
           
           
        
        </div>
    </>
    )
    }
}

export default UserBar
