import React, { Component } from 'react';
import defaultImg from '../images/Logomini.svg';
import _ from 'lodash';
import Modal from 'react-modal';
import Zoom from 'react-reveal/Zoom';
import Banner from './Banner';
import { MdExpandMore } from 'react-icons/md';


export class UserBar extends Component {
    constructor(){
        super()
        this.state = {
            modal: false,
            user: {
                email: '',
                password: '',
                nickname: ''
            }
        }
    }
    clickModal = () => {
        this.setState ({ modal: !this.state.modal });
    };
    onSubmit = (e) => {
        e.preventDefault();
        const { user } = this.state;

    }
    render() {
        const { modal } = this.state;
        const { store } = this.props;
        const me = store.getCurrentUser();
        const profileImg = _.get(me, 'avatar');
        return (
            <>
                <div className="user-bar">
                    { !me ? <button type="button" onClick ={() => this.clickModal() } className="login-btn">Sign In</button> : null}
                    <div className="name-user">{_.get(me, 'name')}</div>
                    <img src={profileImg ? profileImg : defaultImg} alt="user-img" className="img-user"></img>
                </div>
                { modal ?<div className="Modal">
                <Modal isOpen={true}  onRequestClose= {this.closeModal}>
                    <Zoom>
                        <div className="login-modal">
                        <button className="close-modal" onClick={() => this.clickModal()}><MdExpandMore/></button>
                             <Banner title="nDsApp Chat" subtitle="Login/Regiter in a Jiffy">
                                 <div className="user-form">
                                     <form onSubmit={this.onSubmit} method="post">
                                        {/* <div className=" form-item">
                                            <label>Name</label>
                                            <textarea onChange={ this.onTxtfieldChange } name="name" required type="text" placeholder="Name/Nickname"></textarea>
                                        </div> */}
                                        <div className=" form-item">
                                            <label>Email</label>
                                            <textarea onChange={ this.onTxtfieldChange } name="email" required type="email" placeholder="example.vn@gmail.com.vn"></textarea>
                                        </div>
                                        <div className=" form-item">
                                            <label>Password</label>
                                            <textarea onChange={ this.onTxtfieldChange } name="password" required type="password" placeholder="Your-Password"></textarea>
                                        </div>
                                        <div className="form-action">
                                            <Link type="button" className="btn-primary">New Account</Link>
                                            <Link type="submit" className="btn-primary">Sign In</Link>
                                        </div>
                                     </form>
                                
                                 </div>
                             </Banner>
                        </div>
                    </Zoom>
                </Modal>
                </div>  
                :
                null
                }
                
            </>
        )
    }
}

export default UserBar
