import React, { Component } from 'react'

export default class UserMenu extends Component {
    constructor(props){
        super(props);
        this.state = {
            message: null,
            showUserFrom : false,
            user: {
                email: '',
                password: '',
                name: ''
            }
    }
}
onClickOutSide = (e) => {
    if(this.ref && !this.ref.contains(e.target)){
        console.log("Here click out side login form!");
        this.setState({
            showUserFrom: false
        })
    }
}
componentDidMount(){
    window.addEventListener('mousedown', this.onClickOutSide);
    console.log('Didmount');
}
componentWillUnmount(){
    window.removeEventListener('mousedown', this.onClickOutSide);
    console.log('Unmount');
}
onSubmit = (e) => {
    const { store } = this.props;
    const { user } = this.state;
    e.preventDefault();
   

}

onTxtfieldChange = (e) => {
    let { user } = this.state;
    const field = e.target.name;
    user[field] = e.target.value;
    this.setState({
        user: user
    });
}

    render() {
        const { user, message } = this.state;
        const { store } = this.props;
        const me = store.getCurrentUser();
        const profileImg = _.get(me, 'avatar');
        return (
            <div className="user-menu">
                { !me && this.state.showUserFrom ? <div className="user-form" ref = {(ref) => this.ref = ref}>
                <form onSubmit= {this.onSubmit}>
                <ul className="form-container">
                    <li>
                        <h4>Your Account</h4>
                    </li>
                    {/* <li>
                        { message ? <p className={classNames('app-msg', _.get(message, 'type'))}>
                            {_.get(message, 'body')}
                        </p> : null } 
                    </li> */}
                    <li>
                        <label htmlFor="text">
                        UserName
                        </label>
                        <input onChange={(e) => this.onTxtfieldChange(e) } 
                        placeholder="example.vn@nds.com" 
                        required type="text" name="name"
                        value = {_.get(user, 'name')} 
                        id="name" ></input>
                        <button type="button" className="login-btn">Change</button>
                    </li>
                    <li>
                        <label htmlFor="password">Password</label>
                        <input onChange={(e) => this.onTxtfieldChange(e) }
                        autoComplete = {'off'} 
                        placeholder="Your-password" 
                        required type="password"
                        value = {_.get(user, 'password')} 
                        id="password" name="password" ></input>
                        <button type="button" className="login-btn">Change</button>
                    </li>
                    <div className="footer-login">
                        <div className="img">
                            <img src={defaultImg} alt="nds"/>
                        </div>
                        <div className="btn-cc">
                        <li>
                        <button type="button" className="login-btn">Logout</button>
                    </li>
                    {/* <li>
                        <button className="login-btn" >Create new account?</button>
                    </li> */}
                        </div>
                    </div>
                </ul>
                </form>
                </div>
                :
                null
            }
            </div>
        )
    }
}
