import React, {useState} from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components';
// import Hero from './Hero';
import Banner from './Banner';
// import Main1 from './Main1';
import axios from 'axios'
import Content1 from './Content1';
import Detail1 from './Detail1';
import Search1 from './Search1';



const App1 = () => {
  const [weather, setWeather] = useState();
  const [coording, setCoord] = useState();
  const [descript, setDescript] = useState([]); 
    const api_call = async e => {
    e.preventDefault();
    const API_KEY = "cc8f77093d29a14ebde27ae45bf28497";
    const url = `https://api.openweathermap.org/data/2.5/weather?q=Hanoi&appid=${API_KEY}`;
    const req = axios.get(url) ;
    const res = await req ;
    // setWeather(res.data.main);
    setCoord(res.data.coord);
    setDescript(res.data.weather);
    setWeather(res.data.main);
    // 
  }
  // useEffect(() => {
  //   api_call()
   
  // }, [])
    return (
        <Wrapper>
          <div className='container'>
            <Banner title="Welcome to Weather App" subtitle="Where you pass the mind?">
              <Content1>
                <Search1 api_call={api_call}/>
                {weather && <Detail1 weather={weather} coording={coording} descript={descript} /> }
              </Content1>
            </Banner>
          </div>
      </Wrapper>
    );
  };
  const Wrapper = styled.section`
    min-height: 100vh;
    display: grid;
    background: var(--clr-grey-5);
    place-items: center;
    .container {
      width: 90vw;
      max-width: 600px;
      text-align: center;
    }
  `;
export default App1
