import React from 'react'
import { MdChat, MdDonutLarge, MdMoreVert } from 'react-icons/md';
import { Avatar, IconButton, InputBase } from "@material-ui/core";
import { Search } from "@material-ui/icons";
import SidebarChats from './SidebarChats';



function Sidebar({user, isUser}) {
    return (
        <div className="sidebar">
            <div className="sidebar_header">
                <Avatar>
                {isUser && user.picture &&  <img src={user.picture} alt={user.name} />}
                </Avatar>
                <div className="sidebar_headerRight">
                    
                    <IconButton>
                        <MdDonutLarge />
                    </IconButton>
                    <IconButton>
                        <MdChat />  
                    </IconButton>
                    <IconButton>
                        <MdMoreVert />  
                    </IconButton>

                </div>
            </div>
            <div className="sidebar_search">
                <form style={{ width: "100%", display: "flex", background: "var(--clr-primary-8)", borderRadius: ".5rem" }} >
                    <Search style={{ alignItems: "center", marginTop: ".4rem" }}/>
                    <InputBase
                    fullWidth
                    placeholder="Search..."
                    autoFocus
                    style={{ color: "#303030", paddingLeft: "8px", marginTop: ".3rem" }}
                    // onSubmit={e => onSearchSubmit(e)}

                    // value={searchQuery}
                    // onChange={onChange}
                    // // on click we will show popper
                    // onClick={() => {
                    //     setSearchState("clicked");
                    //     setPopper(true);
                    // }}
                    />
                </form>
            </div>
            <div className="sidebar_chats">
                <SidebarChats />
                <SidebarChats />
                <SidebarChats />
            </div>
            
        </div>
    )
}

export default Sidebar
